import React, { useState, useContext } from 'react';
import AppContext from '../context/AppContext';


export default function Header() {
  const [name, setName] = useState('')
  const [brand, setBrand]   = useState('');
  const [quantity, setQuantity] = useState(1)
  const [price, setPrice] = useState(1)
  const { actualList, setActualList } = useContext(AppContext);

  function handleChange({ target }) {
    if(target.name === "name") {
      setName(target.value)
    }
    if(target.name === "quantity") {
      setQuantity(target.value)
    }
    if(target.name === "price") {
      setPrice(target.value)
    }
    if(target.name === "brand") {
      setBrand(target.value)
    }
  }

  function clickSubmit() {
    setActualList(
      [...actualList,
        {
          name,
          brand,
          quantity,
          price,
        }
      ]
    )
  }

  return (
  <div>
    <form onSubmit={ clickSubmit }>
      <input name="name" type="text" onChange={ (e) => handleChange(e)} placeholder='Produto' />
      <input name="brand" type="text" onChange={ (e) => handleChange(e)}  placeholder='Marca'/>
      <input name="quantity" type="number" onChange={ (e) => handleChange(e)} placeholder='Quantidade' />
      <input name="price" type="text" onChange={ (e) => handleChange(e)} placeholder='Valor' />
        <button type='submit'>Salver </button>
    </form>

  </div>
  );
}
